import React, { useState } from "react";
import { useParams } from "react-router-dom";
import data from "../datas.json";

const FicheArtisan = () => {
  const { id } = useParams();
  const artisan = data.find(item => item.id === id);

  const [formValues, setFormValues] = useState({
    nom: '',
    prénom: '',
    email: '',
    sujet: '',
    message: ''
  });

  const [emailSent, setEmailSent] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormValues(prevValues => ({
      ...prevValues,
      [name]: value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!event.target.checkValidity()) {
      event.stopPropagation();
      return;
    }

    const formData = { ...formValues, artisanEmail: artisan.email };

    try {
      const response = await fetch('http://localhost:3000/send-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setEmailSent(true);
        setErrorMessage("");
        setFormValues({
          nom: '',
          prénom: '',
          email: '',
          sujet: '',
          message: ''
        });
      } else {
        const result = await response.json();
        setErrorMessage(result.message || "Une erreur s'est produite lors de l'envoi de l'email.");
      }
    } catch (error) {
      console.error('Error sending email:', error);
      setErrorMessage("Une erreur s'est produite lors de l'envoi de l'email.");
    }
  };

  if (!artisan) {
    return <div>Artisan non trouvé</div>;
  }

  // Initialisation de la validation du formulaire
  React.useEffect(() => {
    const forms = document.querySelectorAll('.needs-validation');

    Array.from(forms).forEach(form => {
      form.addEventListener('submit', event => {
        if (!form.checkValidity()) {
          event.preventDefault();
          event.stopPropagation();
        }

        form.classList.add('was-validated');
      }, false);
    });
  }, []);

  return (
    <div>
      <h2>Contacter {artisan.name}</h2>
      <form className="needs-validation" noValidate onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="nom" className="form-label">Nom</label>
          <input 
            type="text"
            className="form-control"
            id="nom"
            name="nom"
            value={formValues.nom}
            onChange={handleChange}
            required
          />
          <div className="invalid-feedback">
            Veuillez entrer votre nom.
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="prénom" className="form-label">Prénom</label>
          <input
            type="text"
            className="form-control"
            id="prénom"
            name="prénom"
            value={formValues.prénom}
            onChange={handleChange}
            required
          />
          <div className="invalid-feedback">
            Veuillez entrer votre prénom.
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            value={formValues.email}
            onChange={handleChange}
            required
          />
          <div className="invalid-feedback">
            Veuillez entrer une adresse email valide.
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="sujet" className="form-label">Sujet</label>
          <input
            type="text"
            className="form-control"
            id="sujet"
            name="sujet"
            value={formValues.sujet}
            onChange={handleChange}
            required
          />
          <div className="invalid-feedback">
            Veuillez entrer le sujet de votre message.
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="message" className="form-label">Message</label>
          <textarea
            className="form-control"
            id="message"
            name="message"
            rows="3"
            value={formValues.message}
            onChange={handleChange}
            required
          ></textarea>
          <div className="invalid-feedback">
            Veuillez entrer votre message.
          </div>
        </div>
        <button type="submit" className="btn btn-primary">Envoyer</button>
      </form>
      {emailSent && <div className="alert alert-success mt-3">Email envoyé avec succès!</div>}
      {errorMessage && <div className="alert alert-danger mt-3">{errorMessage}</div>}
    </div>
  );
};

export default FicheArtisan;
