import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import data from "../datas.json";

const Header = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const filteredData = data.filter(item => {
      return (
        item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.specialty.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.location.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    setSearchResults(filteredData);
  }, [searchTerm]);

  const handleSearchChange = event => {
    setSearchTerm(event.target.value);
  };

  return (
    <header className="header-containeur" id="Top">
      <nav className="navbar navbar-expand-lg nav-pills ">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">
            <img
              src="./Logo.png"
              alt="Bootstrap"
              title="CV Logo"
              width="40"
              height="34"
            />
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarTogglerDemo02"
            aria-controls="navbarTogglerDemo02"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div
            className="nav justify-content-end"
            id="navbarSupportedContent"
          >
            <ul className="navbar-nav me-auto mb-2 mb-lg-0 nav-underline text-uppercase">
              <li className="nav-item">
                <Link className="nav-link" to="/" aria-current="page">
                  Acceuil
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Bâtiment">
                  Bâtiment
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Alimentation">
                  Alimentation
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Fabrication">
                  Fabrication
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Services">
                  Services
                </Link>
              </li>
            </ul>
            <form className="d-flex" role="search">
              <input
                className="form-control me-2"
                type="text"
                placeholder="Rechercher..."
                value={searchTerm}
                onChange={handleSearchChange}
                aria-label="Search"
              />
              {searchTerm && (
                <ul className="search-results">
                  {searchResults.map(item => (
                    <li key={item.id}>
                      <Link to={`/${item.category}`} className="searchList">
                        {item.name} - {item.specialty} - {item.location}
                      </Link>
                    </li>
                  ))}
                </ul>
              )}
              <button
                className="searchButton btn btn-outline-success"
                type="submit"
              >
                Rechercher
              </button>
            </form>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default Header;
