import React from "react";
import { useParams } from "react-router-dom";
import data from "../datas.json";

document.getElementById('contactForm').addEventListener('submit', async (event) => {
  event.preventDefault();
  
  const formData = {
    firstName: document.getElementById('inputFirstName').value,
    lastName: document.getElementById('inputLastName').value,
    email: document.getElementById('inputEmail3').value,
    subject: document.getElementById('inputSujet').value,
    message: document.getElementById('formControlTextarea1').value
  };

  try {
    const response = await fetch('/send-email', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    });

    const result = await response.json();
    alert(result.message);
  } catch (error) {
    console.error('Error:', error);
  }
});

const FicheArtisan = () => {
  const { id } = useParams();
  const artisan = data.find(item => item.id === id);

  if (!artisan) {
    return <div>Artisan non trouvé</div>;
  }

  (() => {
  const forms = document.querySelectorAll('.needs-validation')

  Array.from(forms).forEach(form => {
    form.addEventListener('submit', event => {
      if (!form.checkValidity()) {
        event.preventDefault()
        event.stopPropagation()
      }

      form.classList.add('was-validated')
    }, false)
  })
})()

  return (
      <div className="colorTest">
        <div className="cardStyle row py-3 ">
          <div className="card col border border-dark" style={{width: '18rem'}}>
            <div className="cardColor card-body  text-center">
              <h5 className="card-title text-uppercase">{artisan.name}</h5>
              <h6 className="card-subtitle mb-2 text-body-secondary">Note :{artisan.note}/5 <i className="fa-solid fa-star text-warning"></i></h6>
              <p className="card-text">{artisan.specialty}</p>
              <p className="card-text">{artisan.location}</p>
              <p className="card-text">{artisan.about}</p>
              <a href={artisan.website} className="card-text">{artisan.website}</a>
            </div>
        </div>
      </div>
        <h2 className="ficheArtisanTitle text-dark py-2 mx-4 text-center ">Une demande ? Remplissez le formulaire pour contacter l'artisan !</h2>
          <div className="colorTest2">
            <form  id="contactForm" className="row g-3 needs-validation px-4" novalidate>
              <div className="row g-3">
                <div className="col mb-3">
                  <label for="inputFirstName" className="col-sm-2 col-form-label">Nom :</label>
                  <div className="col-sm-10">
                    <input type="text" className="form-control" placeholder="Nom" aria-label="First name" required/>
                  </div>
                </div>
                <div className="col mb-3">
                  <label for="inputLastName" className="col-sm-2 col-form-label">Prénom :</label>
                  <div className="col-sm-10">
                    <input type="text" className="form-control" placeholder="Prénom" aria-label="Last name" required/>
                  </div>
                </div>
              </div>
              <div className="row mb-3">
                <label for="inputEmail3" className="col-sm-2 col-form-label">Email :</label>
                <div className="col-sm-10">
                  <input type="email" className="form-control" id="inputEmail3" placeholder="exemple@exemple.com" required/>
                </div>
              </div>
              <div className="row mb-3">
                <label for="inputSujet" className="col-sm-2 col-form-label">Sujet du message:</label>
                <div className="col-sm-10">
                  <input type="password" className="form-control" id="inputSujet" placeholder="Objet de votre message" required/>
                </div>
              </div>
              <div className="mb-3">
                <label for="formControlTextarea1" className="form-label">Message :</label>
                <textarea className="form-control" id="formControlTextarea1" rows="3" placeholder="Entrez votre message ici" required/>
              </div>
              <div className="row mb-3">
                <div className="col-sm-10 offset-sm-2">
                  <div className="form-check">
                    <input className="form-check-input" type="checkbox" id="gridCheck1" required/>
                    <label className="form-check-label" for="gridCheck1">
                      En cochant cette case vous affirmez que toutes les informations sont exactes
                    </label>
                  </div>
                </div>
              </div>
              <button type="submit" className="btn btn-primary m-3 px-2">Envoyer</button>
            </form>
          </div>
        </div>
    );
};



export default FicheArtisan;

